import Header from "./component/Header";
import Tasks from "./component/Tasks";
import { useState } from 'react'
import AddTask


from "./component/AddTask";
function App() {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([
    {
        id: 1,
        text: 'Finish coursework',
        reminder: true,
        dueDate: 'Feb 5th at 2:30pm'
    },
    {
        id: 2,
        text: 'School Meeting',
        reminder: true,
        dueDate: 'Feb 6th at 1:30pm'
    },
    {
        id: 3,
        text: 'Finish coursework',
        reminder: true,
        dueDate: 'Oct 11th at 6:30pm'
    },

 ])   
//ADD TASK
const addTask = (task) => {
  // console.log(task)
  const id= Math.floor(Math.random() * 10000) + 1
  const newTask = {  id , ...task }
  setTasks([...tasks, newTask])
}

 //Delete Tasks and change the state 
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id))
  }

  //Toggle Reminder 
  const toggleReminder = (id) => {
    setTasks(tasks.map((task) => (task.id === id ? {...task, reminder: !task.reminder} : task)))
  }

  return (
    <div className="container">
    <Header onAdd={() => setShowAddTask(!showAddTask)} showAdd={setShowAddTask}/>
    {showAddTask && <AddTask onAdd={addTask}/>}
    {tasks.length > 0 ? <Tasks tasks={tasks}
    onDelete={deleteTask} onToggle={toggleReminder}/> : ('No TASKS TO SHOW')}
    </div>

  );
}



export default App;
