import Header from "./component/Header";
import Tasks from "./component/Tasks";
import { useState } from 'react'

function App() {
  const [tasks, setTasks] = useState([
    {
        id: 1,
        text: 'Finish coursework',
        reminder: true,
        priority: 'low',
        dueDate: '2019-01-01'
    },
    {
        id: 2,
        text: 'Finish coursework',
        reminder: false,
        priority: 'low',
        dueDate: '2019-01-01'
    },
    {
        id: 3,
        text: 'Finish coursework',
        reminder: false,
        priority: 'low',
        dueDate: '2019-01-01'
    },

 ])   

 //Delete Tasks and change the state 
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id))

  } 
  return (
    <div className="container">
    <Header/>
    {tasks.length > 0 ? <Tasks tasks={tasks}
    onDelete={deleteTask}/> : ('No TASKS TO SHOW')}
    </div>

  );
}



export default App;
